        $is_updated = false;
        if(!empty($result)){
            foreach($result as $row){
                if(date('Y-m-d H:i:s') > date('Y-m-d H:i:s',strtotime($row->to))){
                    $data = array(
                        'break_status'  =>  '2'
                    );
                    $this->db->where('id', $row->id);
                    $this->db->update('tbl_stylist_short_breaks',$data);

                    if(!$is_updated){
                        $is_updated = true;
                    }
                }
            }

            if($is_updated){
                echo '1';
            }else{
                echo '0';
            }
        }else{
            echo '0';
        }
