                            var package_name= ' (' + $('#add_service_package_name_hidden_' + booking_details_id).val() + ')';
                            var is_package_applicable = '1';
                        }else{
                            var package_amt = '0.00';
                            var package_name = '';
                            var is_package_applicable = '0';
                        }
                    }else{
                        var package_amt = '0.00';
