		}
		if($customer != "" && $customer != '0'){
			$this->db->where('tbl_new_booking.customer_name',$customer);
		}
		if($from_date != "" && $from_date != '0'){
			$this->db->where('DATE(tbl_booking_services_details.service_date) >=',date('Y-m-d',strtotime($from_date)));
