                $this->db->limit(1);
                $latest = $this->db->get('tbl_booking_payment_entry')->row();
                if(!empty($latest)){
                    $total_closing_pending_amount = $total_closing_pending_amount + (float)$latest->closing_pending_amount;
                }
            }
        }        
        return $total_closing_pending_amount;
    }
