    public function formatTime($minutes) {
        if ($minutes < 60) {
            return $minutes . ' mins';
        } else {
            $hours = floor($minutes / 60);
            $mins = $minutes % 60;
            return $hours . ' hr' . ($mins > 0 ? ' ' . $mins . ' mins' : '');
        }
    }
    
